INSERT INTO `danhmuc` (`iddanhmuc`, `tendanhmuc`, `loaidanhmuc`, `mota`) VALUES
(1, 'Daniel Wellington', 'TH', 'Chào đời vào năm 2011 nhưng đến nay đồng hồ Daniel Wellington chính hãng đã tạo nên một cơn sốt và trào lưu mới dành giới trẻ (cả nam và nữ) trên toàn thế giới. Mỗi chiếc đồng hồ Daniel Wellington chính hãng ra đời đó là một nét đẹp và một phong cách mới mà những người đứng đầu thường hiệu này muốn dành tặng cho những tín đồ của họ, điều này cũng giống như một nút nhạc giao hưởng tạo nên những cung bật muôn màu cho người nghe. Nếu bạn chưa sở hữu chiếc đồng hồ DW chính hãng hãy trải nghiệm “cảm giác” đó ngay tại đây…'),
(2, 'Seiko', 'TH', 'TH Seiko'),
(3, 'Sokolov', 'TH', 'Thương hiệu đồng hồ và trang sức lớn nhất châu u với quy mô nhà máy lên đến gần 20.000 m2 tại Nga. Vật liệu chủ yếu mà Sokolov trang bị lên các sản phẩm của mình là Vàng hoặc Bạc 925. Kết hợp với đó là nhiều kỹ thuật tiên tiến như mạ Rhodium, tạo hoa văn Filigree, phủ men, họa tiết vết cắt kim cương,…. Đối tác của Sokolov là Swarovski (Áo) và Ronda (Thụy Sỹ). Chính những điều này đã mang đến chiếc đồng hồ và trang sức Sokolov vừa sang trọng, vừa thời trang mà các thương hiệu khác khó sao chép.'),
(4, 'Bông tai', 'LSP', 'Bông tai đeo ở tai nha mọi người'),
(5, 'Casio', 'TH', 'Đồng hồ Casio được biết đến là thương hiệu đồng hồ lâu đời trong ngành công nghiệp đồng hồ tại Nhật Bản. Những mẫu đồng hồ Casio nam và đồng hồ Casio nữ được yêu thích bởi thiết kế đơn giản, dễ dàng để xem giờ, được trang bị đầy đủ các công nghệ mới nhất và có giá bán cực kỳ rẻ. Ngoài ra, đồng hồ Casio chính hãng còn nổi bật với chức năng đa dạng giúp ích cho người dùng vào những hoạt động đời sống thường ngày. '),
(6, 'Vòng tay, lắc tay', 'LSP', 'Vòng tay đeo cho sang, ra ngoài đường khua tay múa chân vòng vàng lấp lành là người ta thấy giàu liền.'),
(7, 'Citizen', 'TH', 'Được tạo nên từ những chất liệu Hàng Đầu, Citizen giúp đảo bảo song song giữa bộ máy hoạt động mạnh mẽ, bền bỉ và chính xác cùng thiết kế sang trọng, hiện đại và phù hợp với thị hiếu người tiêu dùng. Chưa hết, hãng đồng hồ CITIZEN đã trở thành thương hiệu thời trang hàng đầu nước NHẬT và là người dẫn đầu trong việc sử dụng công nghệ sử dụng năng lượng ánh sáng mặt trời (Citizen ECO-DRIVE). Điều này đã giúp những chiếc đồng hồ Citizen nam nữ, cặp đôi chính hãng trở dòng sản phẩm cực kỳ ưa chuộng cho giới Doanh Nhân'),
(8, 'Đồng hồ', 'LSP', 'những mẫu đồng hồ được sản xuất trên cơ sở chú trọng nhiều về thiết kế bên ngoài, cũng như chú trọng bắt nhịp theo xu hướng thời trang hơn là về việc cải tiến, phát triển những chức năng và bộ máy trang bị.');
